package com.dimovsoft.st25dv_i2cimport android.app.Applicationimport com.dimovsoft.st25dv_i2c.data.DatabaseRepositoryimport com.dimovsoft.st25dv_i2c.data.RealmDatabaseimport com.dimovsoft.st25dv_i2c.security.SecurityManagerimport org.koin.android.ext.koin.androidContextimport org.koin.android.ext.koin.androidLoggerimport org.koin.core.context.GlobalContext.startKoinimport org.koin.dsl.moduleclass MyApplication : Application() {	override fun onCreate() {		super.onCreate()		initKoin()	}	private fun initKoin() {		startKoin {			androidLogger()			androidContext(applicationContext)			modules(				module {					single<SecurityManager> { SecurityManager() }					single<RealmDatabase> { RealmDatabase(get()) }					single<DatabaseRepository> { DatabaseRepository(get()) }				}			)		}	}}