package com.dimovsoft.st25dv_i2c.modelsimport com.st.smartaglib.model.SensorDataSampleimport io.realm.kotlin.types.RealmObjectimport io.realm.kotlin.types.annotations.PrimaryKeyimport org.mongodb.kbson.BsonObjectIdimport org.mongodb.kbson.ObjectIdimport java.util.Dateclass RealmDataSample: RealmObject {	@PrimaryKey	var _id: ObjectId = BsonObjectId()	var date: Long = Date().time	var temperature: Float? = null	var pressure: Float? = null	var humidity: Float? = null	var acceleration: Float? = null	override fun equals(other: Any?): Boolean =		other is RealmDataSample && hashCode() == other.hashCode()	override fun hashCode(): Int {		var result = _id.hashCode()		result = 31 * result + date.hashCode()		result = 31 * result + (temperature?.hashCode() ?: 0)		result = 31 * result + (pressure?.hashCode() ?: 0)		result = 31 * result + (humidity?.hashCode() ?: 0)		result = 31 * result + (acceleration?.hashCode() ?: 0)		return result	}	companion object {		fun from(sample: SensorDataSample) =			RealmDataSample().apply {				date = sample.date.time				temperature = sample.temperature				pressure = sample.pressure				humidity = sample.humidity				acceleration = sample.acceleration			}	}}