package com.dimovsoft.st25dv_i2c.navigationimport androidx.fragment.app.FragmentManagerimport com.dimovsoft.st25dv_i2c.Rimport com.dimovsoft.st25dv_i2c.ui.LoginFragmentimport com.dimovsoft.st25dv_i2c.ui.MainFragmentfun FragmentManager.gotoLogin() {	beginTransaction()		.replace(R.id.main, LoginFragment.newInstance())		.commit()}fun FragmentManager.gotoMain() {	beginTransaction()		.replace(R.id.main, MainFragment.newInstance())		.commit()}